import './MoviesCardList.css';
import MoviesCard from '../MoviesCard/MoviesCard';
import { useState } from 'react';

import {movies} from '../../../vendor/fish-data'

function MoviesCardList() {

  const [cards, setCards] = useState(movies);

  const handleCardDelete = (_id) => {setCards((cards) => cards.filter((card) => card._id !== _id))}

  return (
    <ul className='movies'>
      {movies.map((movies) =>

        <MoviesCard movies={movies} key={movies.id} handleCardDelete={handleCardDelete} />

      )}
    </ul>
  );
}

export default MoviesCardList;
